<?phpclass FacultyCoursesAccessControlComponent extends Object {  var $FacultyAcoUser;    function __construct() {  	$this->FacultyAcoUser = ClassRegistry::init('FacultyAcoUser');  }    function grantAccessPermission($facultyAcoId, $userId) {	$data['faculty_aco_id'] = $facultyAcoId;	$data['user_id'] = $userId;	$data['_create'] = 1;	$data['_read'] = 1;	$data['_update'] = 1;	$data['_delete'] = 1;	// Saves only if it's the highest access permission 	$topAccessLv = $this->FacultyAcoUser->getUserTopAccessLevel($userId);	  	if($facultyAcoId > $topAccessLv || empty($topAccessLv)) {  	  $this->FacultyAcoUser->save($data);  	  return true;	  	} else {  	  return false;  	}  }    function denyAccessPermission($facultyAcoId, $userId) {  	$data['faculty_aco_id'] = $facultyAcoId;	$data['user_id'] = $userId;	$data['_create'] = -1;	$data['_read'] = -1;	$data['_update'] = -1;	$data['_delete'] = -1;  	// Save only if it's the highest deny permission	$topDenyLevel = $this->FacultyAcoUser->getUserTopDenyLevel($useId);	if($facultyAcoId > $topDenyLevel || empty($topDenyLevel)) {	  $this->FacultyAcoUser->save($data);	  return true;		} else {	  return false;	}  }}?>