<?phpclass FacultyAcoUser extends AppModel{  var $name = 'FacultyAcoUser';    function getAccessPermissionByFacultyAcoIdUserId($faculty_aco_id=null, $user_id=null) {      return $this->find('first', array('conditions' =>     			   	array('faculty_aco_id' => $faculty_aco_id, 'dept_id' => $user_id)));  }    function getAllChildren($parentNodeId) {  	return $this->children($parentNodeId);  }    function getAco($userId)  {    return $this->find('first', array('conditions' => array('dept_id' => $userId), 'fields' => array('faculty_aco_id')));  }
}?>