<?phpclass FacultyAcoUser extends AppModel{	  var $actsAs = array('Tree');  var $name = 'FacultyAcoUser';    function getUserTopAccessLevel($user_id) {  	return $this->find('all', array(  			'conditions' => array('user_id' => $user_id, '_create' => 1, '_read' => 1, '_update' => 1, '_delete' => 1),  			'fields' => array('MIN(faculty_aco_id)')  	));  }    function getUserTopDenyLevel($user_id) {  	return $this->find('all', array(  			'conditions' => array('user_id' => $user_id, '_create' => -1, '_read' => -1, '_update' => -1, '_delete' => -1),  			'fields' => array('MIN(faculty_aco_id)')  	));  }    function getAllChildren($parentNodeId) {  	return $this->children($parentNodeId);  }    }?>